script:
function Schlafmodus() 
{ 

bestaetigen=confirm("Schlafmodus Aktivieren?"); 

if (bestaetigen==true) 
{ 
program.sleepMode = true; 
program.sleepMinutes = 90; 
} 
else 
{ 
alert("Sie haben auf ABBRECHEN geklickt"); 
} 
} 







